import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [tasks, setTasks] = useState([
    { id: 1, title: 'Task 1', description: 'Lorem Ipsum has been the industry\'s standard dummy text ever since the 1500s,', dueDate: '2024-07-30', priority: 'High', completed: false },
    { id: 2, title: 'Task 2', description: 'Description for Task 2', dueDate: '2024-07-25', priority: 'High', completed: false },
    { id: 3, title: 'Task 3', description: 'Description for Task 3', dueDate: '2024-08-01', priority: 'Medium', completed: false },
    { id: 4, title: 'Task 4', description: 'Description for Task 4', dueDate: '2024-08-12', priority: 'Medium', completed: false },
  ]);
  const [filter, setFilter] = useState('All');
  const [expandedTask, setExpandedTask] = useState(null);
  const [isAddingTask, setIsAddingTask] = useState(false);
  const [editingTask, setEditingTask] = useState(null);
  const [newTask, setNewTask] = useState({});
  const [darkMode, setDarkMode] = useState(false);

  useEffect(() => {
    document.body.classList.toggle('dark', darkMode);
  }, [darkMode]);

  const filteredTasks = tasks.filter(task => {
    if (filter === 'All') return true;
    if (filter === 'Done') return task.completed;
    return task.priority === filter && !task.completed;
  });

  const toggleTaskExpansion = (id) => {
    setExpandedTask(expandedTask === id ? null : id);
  };

  const addTask = () => {
    if (newTask.title && newTask.dueDate && newTask.priority) {
      setTasks([...tasks, { ...newTask, id: tasks.length + 1, completed: false }]);
      setNewTask({});
      setIsAddingTask(false);
    }
  };

  const deleteTask = (id) => {
    setTasks(tasks.filter(task => task.id !== id));
  };

  const toggleTaskCompletion = (id) => {
    setTasks(tasks.map(task => 
      task.id === id ? { ...task, completed: !task.completed } : task
    ));
  };

  const startEditingTask = (task) => {
    setEditingTask(task);
    setExpandedTask(task.id);
  };

  const saveEditedTask = () => {
    if (editingTask) {
      setTasks(tasks.map(task => 
        task.id === editingTask.id ? editingTask : task
      ));
      setEditingTask(null);
    }
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', { day: 'numeric', month: 'short', year: 'numeric' });
  };

  return (
    <div className={`App ${darkMode ? 'dark' : ''}`}>
      <div className="container">
        <div className="header">
          <h1>Task List View</h1>
          <button className="icon-button" onClick={() => setDarkMode(!darkMode)}>
            {darkMode ? '‚òÄÔ∏è' : 'üåô'}
          </button>
        </div>
        <button className="add-task-button" onClick={() => setIsAddingTask(true)}>
          + Add New Task
        </button>
        <div className="filter-buttons">
          {['All', 'High', 'Medium', 'Low', 'Done'].map(priority => (
            <button
              key={priority}
              className={`filter-button ${filter === priority ? 'active' : ''}`}
              onClick={() => setFilter(priority)}
            >
              {priority}
            </button>
          ))}
        </div>
        {isAddingTask && (
          <div className="add-task-form">
            <h2>Add New Task</h2>
            <input
              type="text"
              placeholder="Task Name"
              value={newTask.title || ''}
              onChange={e => setNewTask({...newTask, title: e.target.value})}
            />
            <textarea
              placeholder="Description"
              value={newTask.description || ''}
              onChange={e => setNewTask({...newTask, description: e.target.value})}
            />
            <div className="date-input">
              <span>üìÖ</span>
              <input
                type="date"
                value={newTask.dueDate || ''}
                onChange={e => setNewTask({...newTask, dueDate: e.target.value})}
              />
            </div>
            <select
              value={newTask.priority || ''}
              onChange={e => setNewTask({...newTask, priority: e.target.value})}
            >
              <option value="">Select Priority</option>
              <option value="High">High</option>
              <option value="Medium">Medium</option>
              <option value="Low">Low</option>
            </select>
            <div className="form-buttons">
              <button onClick={addTask}>Add Task</button>
              <button onClick={() => setIsAddingTask(false)}>Cancel</button>
            </div>
          </div>
        )}
        {filteredTasks.map(task => (
          <div key={task.id} className="task-card">
            <div className="task-header">
              <div className="task-title-date">
                <input
                  type="checkbox"
                  checked={task.completed}
                  onChange={() => toggleTaskCompletion(task.id)}
                />
                <h3 className={task.completed ? 'completed' : ''}>{task.title}</h3>
                <span className="due-date">Due Date: {formatDate(task.dueDate)}</span>
              </div>
              <div className="task-priority-expand">
                <span className={`priority ${task.priority.toLowerCase()}`}>
                  {task.priority}
                </span>
                <button className="expand-button" onClick={() => toggleTaskExpansion(task.id)}>
                  {expandedTask === task.id ? '‚ñ≤' : '‚ñº'}
                </button>
              </div>
            </div>
            {expandedTask === task.id && (
              <div className="task-details">
                {editingTask && editingTask.id === task.id ? (
                  <div className="edit-task-form">
                    <input
                      type="text"
                      value={editingTask.title}
                      onChange={e => setEditingTask({...editingTask, title: e.target.value})}
                    />
                    <textarea
                      value={editingTask.description}
                      onChange={e => setEditingTask({...editingTask, description: e.target.value})}
                    />
                    <div className="date-input">
                      <span>üìÖ</span>
                      <input
                        type="date"
                        value={editingTask.dueDate}
                        onChange={e => setEditingTask({...editingTask, dueDate: e.target.value})}
                      />
                    </div>
                    <select
                      value={editingTask.priority}
                      onChange={e => setEditingTask({...editingTask, priority: e.target.value})}
                    >
                      <option value="High">High</option>
                      <option value="Medium">Medium</option>
                      <option value="Low">Low</option>
                    </select>
                    <div className="form-buttons">
                      <button onClick={saveEditedTask}>Save</button>
                      <button onClick={() => setEditingTask(null)}>Cancel</button>
                    </div>
                  </div>
                ) : (
                  <>
                    <p>{task.description}</p>
                    <div className="task-actions">
                      <button onClick={() => startEditingTask(task)}>Edit</button>
                      <button onClick={() => deleteTask(task.id)}>Delete</button>
                    </div>
                  </>
                )}
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
